import { useParams } from "react-router";
import getRecipeById from "../data";
import React, { useState, useEffect } from "react";

function Recipe() {
	const { recipeId } = useParams();

	const [recipeTitle, setRecipeTitle] = useState(getRecipeById(recipeId).title);
	const [recipeIngredients, setRecipeIngredients] = useState(getRecipeById(recipeId).ingredients);
	const [recipeInstructions, setRecipeInstructions] = useState(getRecipeById(recipeId).instructions);
	const [recipeImage, setRecipeImage] = useState(getRecipeById(recipeId).image);

	useEffect(() => {
		const recipe = getRecipeById(recipeId);
		setRecipeTitle(recipe.title);
	}, [recipeId]);

	return (
		<div>
			<h1> {recipeTitle}</h1>
		</div>
	);
}
export default Recipe;
