import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";
import useAnnualTax from "./useAnnualTax";
import Student from "./Student";

function App() {
	// function getStudents() {
	//   axios.get('http://apps.loopevo.com/apis/students/students.php')
	//   .then((response) => {
	//     console.log(response.data);
	//   })
	//   .catch((error) => {
	//     console.log(error);
	//   })
	// }
	const [data, setData] = useState([]);
	const annualTax = useAnnualTax();

	async function getStudents() {
		const response = await axios("http://apps.loopevo.com/apis/students/students.php");
		console.log(response.data);
		setData(response.data);
	}

	useEffect(() => {
		getStudents();
	}, []);

	if (data.length === 0) {
		return <h1>Loading</h1>;
	}

	return (
		<div className="App">
			<h1>Students list:</h1>
			<ul>
				{data.map((student) => (
					<Student data={student} key={student.id} />
				))}
			</ul>
		</div>
	);
}

export default App;
