import React from "react";
import Carousel from 'react-bootstrap/Carousel';
import getProductsForCategory from "../productList";
 
function Promotions() {

	function randomProduct(categoryId) {
		let products = getProductsForCategory(categoryId);
		console.log(products);
		let randomIndex = Math.floor(Math.random()*getProductsForCategory(categoryId).length);
		return products[randomIndex];
	}

	return (
		<div>
			<h1>Test</h1>
		<Carousel className="carousel-container">
			<Carousel.Item>
				<img src={randomProduct(1).image} className="d-block w-100" alt={randomProduct(1).name} />
			</Carousel.Item>

			<Carousel.Item>
				<img src={randomProduct(2).image} className="d-block w-100" alt={randomProduct(2).name} />
			</Carousel.Item>

			<Carousel.Item>
				<img src={randomProduct(3).image} className="d-block w-100" alt={randomProduct(3).name} />
			</Carousel.Item>
		</Carousel>
		</div>
	);
}

export default Promotions;
