import React, { useState } from "react";
import "./ChooseAnimal.css";

function ChooseAnimal() {
	const animals = ["dog", "cat"];
	const colors = ["black", "white", "brown"];

	const [chosenAnimal, setChosenAnimal] = useState(animals[0]);
	const [chosenColor, setChosenColor] = useState(colors[0]);
	const [animalName, setAnimalName] = useState("");
	const [firstName, setFirstName] = useState("");
	const [lastName, setLastName] = useState("");

	return (
		<div className="search-params">
			<form>
				<label>Animal:</label>
				<select>
					{animals.map((animal, index) => (
						<option key={index} value={animal}>
							{animal}
						</option>
					))}
				</select>
				<label>Color:</label>
				<select>
					{colors.map((color, index) => (
						<option key={index} value={color}>
							{color}
						</option>
					))}
				</select>
				<label>
					Give a name to the {chosenAnimal} {chosenColor}:
				</label>
				<input type="text" value={animalName}></input>
				<label>Your first name:</label>
				<input type="text" value={firstName}></input>
				<label>Your last name:</label>
				<input type="text" value={lastName}></input>
			</form>
		</div>
	);
}

export default ChooseAnimal;
