import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";

function App() {
	// function getStudents() {
	//   axios.get('http://apps.loopevo.com/apis/students/students.php')
	//   .then((response) => {
	//     console.log(response.data);
	//   })
	//   .catch((error) => {
	//     console.log(error);
	//   })
	// }
	const [data, setData] = useState([]);

	async function getStudents() {
		const response = await axios("http://apps.loopevo.com/apis/students/students.php");
		console.log(response.data);
		setData(response.data);
	}

	useEffect(() => {
		getStudents();
	}, []);

	if (data.length === 0) {
		return <h1>Loading</h1>;
	}

	return (
		<div className="App">
			<h1>Students list:</h1>
			<ul>
				{data.map((student) => (
					<li>
						<b>ID:</b>
						{student.id} <b>First_name:</b> {student.first_name} <b>Last_name:</b>
						{student.last_name} <b>Email:</b>
						{student.email}
					</li>
				))}
			</ul>
		</div>
	);
}

export default App;
