import "./App.css";
import axios from "axios";
import React, { useState, useEffect } from "react";

function App() {
	// function getStudents() {
	//   axios.get('http://apps.loopevo.com/apis/students/students.php')
	//   .then((response) => {
	//     console.log(response.data);
	//   })
	//   .catch((error) => {
	//     console.log(error);
	//   })
	// }
	const [data, setData] = useState([]);

	async function getStudents() {
		const response = await axios("http://apps.loopevo.com/apis/students/students.php");
		console.log(response);
		return response.data;
	}

	useEffect(() => {
		const dataResponse = getStudents();
		setData(dataResponse);
	}, []);

	return (
		<div className="App">
			<h1>Students list:</h1>
			<ul>
				{data.map((student) => (
					<li>
						ID:{student.id} First_name: {student.first_name} Last_name:{student.last_name} Email:{student.email}
					</li>
				))}
			</ul>
		</div>
	);
}

export default App;
