import React from "react";

function Card(props) {


    const replaceK = function () { // ???

        let splitArray = props.user.followers.toString().split('');
        console.log(splitArray);
        let finalFolowers = splitArray.slice(0, splitArray.length - 3).toString() + 'K';
        return finalFolowers
        
    }

    return (
        <section className="platform">
            <div className="icon" >
                <img src={props.icon}></img>
                <p>{props.user.name}</p>
            </div>
            <div className="followers">
                <button className="update-followers-button">-</button>
                {props.user.followers > 9999 ? replaceK() : props.user.followers}
                <button className="update-followers-button">+</button>
            </div>
            <div className="subscribers">
                F O L O W E R S
            </div>
            <div>
                <span className='trend-ascend'>▼</span>
                <span className='trend-descend'>▼</span>
                {props.user.difference}
            </div>
        </section>
    )
}

export default Card;
