import React, { useState, useRef } from "react";
import "./App.css";

function App() {
	// const [item, setItem] = useState("");
	const [items, setItems] = useState([]);
	const [selectedText, setSelectedText] = useState("");

	const inputRef = useRef(null);

	const addItem = function (e) {
		e.preventDefault();
		// setItems(items.push(item));
		// items.push(item);
		setItems([...items, inputRef.current.value]);
		inputRef.current.value = "";
		// console.log(items);
		inputRef.current.focus();
	};

	const edit = function (index) {
		setSelectedText(items[index]);
	};

	return (
		<div className="container">
			<form>
				<label>To do list</label>
				{/* <input ref={inputRef} type="text" onChange={(e) => setItem(e.target.value)}></input> */}
				<input ref={inputRef} value={selectedText} type="text"></input>
				<button onClick={(e) => addItem(e)}>Add me</button>
				<ul>
					{items.map((element, index) => (
						<li onClick={(index) => edit(index)} key={index}>
							{element}
						</li>
					))}
				</ul>
			</form>
		</div>
	);
}

export default App;
